function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var x=g_form.getValue("favourite_hero");
	g_form.clearOptions("favourite_movie");
	if(x=="ntr")
		{
			g_form.addOption("favourite_movie","nannaku prematho","Nannaku Prematho");
			g_form.addOption("favourite_movie","aravindha sametha","Aravindha Sametha");
			g_form.addOption("favourite_movie","brundavanam","Brundavanam");
			g_form.addInfoMessage("Best Choice");
		}
	if(x=="mahesh babu")
		{
			g_form.addOption("favourite_movie","maharshi","Maharshi");
			g_form.addOption("favourite_movie","srimanthudu","Srimanthudu");
			g_form.addOption("favourite_movie","bharath ane nenu","Bharath Ane Nenu");	
		}
	if(x=="ram")
		{
			g_form.addOption("favourite_movie","pandaga chesko","Pandaga Chesko");
			g_form.addOption("favourite_movie","hello guru premakosame","Hello Guru Premakosame");
			g_form.addOption("favourite_movie","vunnadhi okate jindagi","Unnadhi Okate Jindagi");	
			g_form.addErrorMessage("Better To Select Another Option");
			g_form.addInfoMessage("Best choice123");
		}


   //Type appropriate comment here, and begin script below

}
